// 3. code that generates an array of numbers - from n to m
function exercise3() {
  // TODO: declare variables n and m of type number
  // TODO: assign n and m some values - n = 1, m = 10
  const n: number = 1;
  const m: number = 10;
  // TODO: declare variable result of type array of numbers
  const arr: number[] = [];
  // TODO: create a for loop that adds numbers from n to m to result array
  for (let i = n; i <= m; i++) {
    arr.push(i);
  }
  console.log(arr);
  // TODO: support case where m > n (reverse the order)
  const reversedArr: number[] = [];
  for (let i = m; i >= n; i--) {
    reversedArr.push(i);
  }
  // TODO: print result array to console
  console.log(reversedArr);
}
exercise3();
